import React, { useState, useEffect } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:8000';

function App() {
  const [query, setQuery] = useState('');
  const [answer, setAnswer] = useState('');
  const [sources, setSources] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [documents, setDocuments] = useState([]);
  const [newDocument, setNewDocument] = useState('');
  const [documentMetadata, setDocumentMetadata] = useState('');
  const [activeTab, setActiveTab] = useState('query');
  const [error, setError] = useState('');

  // Fetch documents on component mount
  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/documents`);
      if (response.ok) {
        const data = await response.json();
        setDocuments(data.documents || []);
      }
    } catch (err) {
      console.error('Error fetching documents:', err);
    }
  };

  const handleQuery = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;

    setIsLoading(true);
    setError('');
    
    try {
      const response = await fetch(`${API_BASE_URL}/query`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: query,
          max_results: 5
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setAnswer(data.answer);
        setSources(data.sources || []);
      } else {
        const errorData = await response.json();
        setError(errorData.detail || 'An error occurred while processing your query.');
      }
    } catch (err) {
      setError('Failed to connect to the server. Make sure the backend is running.');
      console.error('Query error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddDocument = async (e) => {
    e.preventDefault();
    if (!newDocument.trim()) return;

    setIsLoading(true);
    setError('');

    try {
      let metadata = {};
      if (documentMetadata.trim()) {
        try {
          metadata = JSON.parse(documentMetadata);
        } catch (err) {
          setError('Invalid JSON in metadata field');
          setIsLoading(false);
          return;
        }
      }

      const response = await fetch(`${API_BASE_URL}/documents`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newDocument,
          metadata: metadata
        }),
      });

      if (response.ok) {
        setNewDocument('');
        setDocumentMetadata('');
        fetchDocuments(); // Refresh the documents list
        setError('');
      } else {
        const errorData = await response.json();
        setError(errorData.detail || 'Failed to add document.');
      }
    } catch (err) {
      setError('Failed to connect to the server. Make sure the backend is running.');
      console.error('Add document error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteDocument = async (documentId) => {
    if (!window.confirm('Are you sure you want to delete this document?')) {
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/documents/${documentId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        fetchDocuments(); // Refresh the documents list
      } else {
        const errorData = await response.json();
        setError(errorData.detail || 'Failed to delete document.');
      }
    } catch (err) {
      setError('Failed to connect to the server.');
      console.error('Delete document error:', err);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ü§ñ RAG Application</h1>
        <p>Retrieval-Augmented Generation Demo</p>
      </header>

      <main className="App-main">
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 'query' ? 'active' : ''}`}
            onClick={() => setActiveTab('query')}
          >
            Query Documents
          </button>
          <button 
            className={`tab ${activeTab === 'manage' ? 'active' : ''}`}
            onClick={() => setActiveTab('manage')}
          >
            Manage Documents
          </button>
        </div>

        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}

        {activeTab === 'query' && (
          <div className="query-section">
            <div className="query-form">
              <h2>Ask a Question</h2>
              <form onSubmit={handleQuery}>
                <div className="input-group">
                  <textarea
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Enter your question here..."
                    rows="3"
                    disabled={isLoading}
                  />
                </div>
                <button type="submit" disabled={isLoading || !query.trim()}>
                  {isLoading ? 'ü§î Thinking...' : 'üîç Search'}
                </button>
              </form>
            </div>

            {answer && (
              <div className="results">
                <div className="answer-section">
                  <h3>üí° Answer</h3>
                  <div className="answer-content">
                    {answer}
                  </div>
                </div>

                {sources.length > 0 && (
                  <div className="sources-section">
                    <h3>üìö Sources</h3>
                    <div className="sources-list">
                      {sources.map((source, index) => (
                        <div key={index} className="source-item">
                          <strong>Source {index + 1}:</strong> {source}
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        )}

        {activeTab === 'manage' && (
          <div className="manage-section">
            <div className="add-document">
              <h2>Add New Document</h2>
              <form onSubmit={handleAddDocument}>
                <div className="input-group">
                  <label>Document Content:</label>
                  <textarea
                    value={newDocument}
                    onChange={(e) => setNewDocument(e.target.value)}
                    placeholder="Enter document content..."
                    rows="6"
                    disabled={isLoading}
                  />
                </div>
                <div className="input-group">
                  <label>Metadata (Optional JSON):</label>
                  <textarea
                    value={documentMetadata}
                    onChange={(e) => setDocumentMetadata(e.target.value)}
                    placeholder='{"topic": "example", "author": "John Doe"}'
                    rows="2"
                    disabled={isLoading}
                  />
                </div>
                <button type="submit" disabled={isLoading || !newDocument.trim()}>
                  {isLoading ? '‚è≥ Adding...' : 'üìÑ Add Document'}
                </button>
              </form>
            </div>

            <div className="documents-list">
              <h2>Knowledge Base ({documents.length} documents)</h2>
              {documents.length === 0 ? (
                <p className="empty-state">
                  No documents in the knowledge base yet. Add some documents to get started!
                </p>
              ) : (
                <div className="documents-grid">
                  {documents.map((doc) => (
                    <div key={doc.id} className="document-card">
                      <div className="document-content">
                        <strong>Preview:</strong>
                        <p>{doc.content_preview}</p>
                        {doc.metadata && Object.keys(doc.metadata).length > 0 && (
                          <div className="document-metadata">
                            <strong>Metadata:</strong>
                            <pre>{JSON.stringify(doc.metadata, null, 2)}</pre>
                          </div>
                        )}
                        <small>Created: {new Date(doc.created_at).toLocaleString()}</small>
                      </div>
                      <button
                        className="delete-button"
                        onClick={() => handleDeleteDocument(doc.id)}
                        title="Delete document"
                      >
                        üóëÔ∏è
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
